#!/bin/bash
#SBATCH --time=5:00:00
#SBATCH --ntasks=8
#SBATCH --mem=32g
#SBATCH --tmp=32g
#SBATCH --mail-type=ALL
#SBATCH --mail-user=email
#SBATCH -e Name.out
#SBATCH -o Name.err

cd directory/with/fastq
module load conda
source activate crispress02_env #CRISPResso environment

#CHLI2
CRISPResso --fastq_r1 R1_001.fastq.gz --fastq_r2 R2_001.fastq.gz --amplicon_seq  --guide_seq CGACATTCATAACAGAGACA --name Temperature_Duration_Genotype-Construct_Site_Sample --ignore_substitutions --quantification_window_size 3 --output_folder /data/

