CRISPResso --fastq_r1 1_R1_001.fastq.gz --fastq_r2 1_R2_001.fastq.gz --amplicon_seq  --guide_seq GTTTTCTCGCACTTAAGCTC --name Temperature_Duration_Genotype-Construct_Site_Sample --ignore_substitutions --quantification_window_size 3 --min_identity_score 90 --output_folder /data/

